
public class calculator {

	public static void main(String[] args) {
		int a = Integer.parseInt(args[0]);
		int b = Integer.parseInt(args[1]);
		float c;
		System.out.println(c = a + b);
		System.out.println(c = a - b);
		System.out.println(c = a * b);
		System.out.println(c = a / b);
		// TODO Auto-generated method stub

	}

}
